create table cidades (estado_id bigint not null, id bigint generated by default as identity, nome varchar(255) not null, primary key (id));
create table cozinhas (id bigint generated by default as identity, nome varchar(255) not null, primary key (id));
create table estados (id bigint generated by default as identity, nome varchar(255) not null, primary key (id));
create table forma_pagamentos (id bigint generated by default as identity, descricao varchar(255) not null, primary key (id));
create table grupo (data_atualizacao timestamp(6), data_cadastro timestamp(6), id bigint generated by default as identity, nome varchar(255) not null, primary key (id));
create table grupo_permissao (grupo_id bigint not null, permissao_id bigint not null);
create table permissaos (id bigint generated by default as identity, descricao varchar(255) not null, nome varchar(255) not null, primary key (id));
create table produtos (ativo boolean not null, preco float(53), id bigint generated by default as identity, restaurante_id bigint, descricao varchar(255), nome varchar(255), primary key (id));
create table restaurante_forma_pagamento (forma_pagamento_id bigint not null, restaurante_id bigint not null);
create table restaurantes (taxa_frete numeric(38,2) not null, cozinha_id bigint, data_atualizacao timestamp(6) not null, data_cadastro timestamp(6) not null, endereco_cidade_id bigint, id bigint generated by default as identity, endereco_bairro varchar(255), endereco_cep varchar(255), endereco_complemento varchar(255), endereco_logradouro varchar(255), endereco_numero varchar(255), nome varchar(255) not null, primary key (id));
create table usuario (data_cadastro timestamp(6) not null, id bigint generated by default as identity, email varchar(255) not null, nome varchar(255) not null, senha varchar(255) not null, primary key (id));
create table usuario_grupo (grupo_id bigint not null, usuario_id bigint not null);
alter table if exists cidades add constraint FKdt0b3ronwpi1upsrhaeq6r69n foreign key (estado_id) references estados;
