alter table if exists grupo_permissao add constraint FKta4si8vh3f4jo3bsslvkscc2m foreign key (grupo_id) references grupo;
alter table if exists produtos add constraint FKsl3jhd8nhd103c5nm6yocnnkx foreign key (restaurante_id) references restaurantes;
