alter table if exists restaurante_forma_pagamento add constraint FKm6ricm3gnnj4e3ev82495metc foreign key (restaurante_id) references restaurantes;
alter table if exists restaurantes add constraint FKgdtjhnl3atr87y5s0tcnr2i5q foreign key (cozinha_id) references cozinhas;
alter table if exists restaurantes add constraint FKjxh9a5cs0ke85v1t0uh6p9shb foreign key (endereco_cidade_id) references cidades;
